import React, { useState } from 'react';
import './App.css';
import { web3, portis } from './services/web3';
import Address from './components/address';
import Balance from './components/balance';
import { getAddressBalances } from 'eth-balance-checker/lib/web3';

type IState = {
  address: string;
  balance: number;
}

const initialState: IState = {
  address: "",
  balance: 0
}

function App() {

  const [state, setState] = useState(initialState);
  const tokens = ['0x0'];//,'0x6ce8dA28E2f864420840cF74474eFf5fD80E65B8', '0xd66c6B4F0be8CE5b39D52E0Fd1344c389929B378', '0xEC5dCb5Dbf4B114C9d0F65BcCAb49EC54F6A0867'];
  const RPCURL = portis.provider;
  web3.eth.getAccounts((accounts: any) => {
    console.log(accounts);
  });
  portis.showPortis();
  portis.onLogin((address) => {
    web3.eth.getBalance(address).then((balance) => {
      setState({ balance: Number(web3.utils.fromWei(balance)), address: address })
    });

  });


  return (
    <div className="App">
      <header className="App-header">
        <Address address={state.address}></Address>
        <Balance amount={state.balance} symbol={"ETH"}></Balance>
      </header>
    </div>
  );
}

export default App;
