import Portis from '@portis/web3';
import Web3 from 'web3';



export const portis = new Portis('1eec0edb-8b04-4423-894f-98b83d2bccd7', 'mainnet');
const customNode = {
    nodeUrl: 'https://awesomenode.com',
    chainId: 127,
  };
  portis.changeNetwork(customNode);
export const web3 = new Web3(portis.provider);
